mapper.select:
select gu.user_id as userId,gu.*
from g_user gu
where gu.identity_code = #{identityCode}

select gu.user_id as userId,gu.*
from g_user gu
where gu.user_id = #{userId}

select gu.user_id as userId,gu.*
from g_user gu
where gu.email = #{email}

select gu.user_id as userId,gu.*
from g_user gu
where gu.phone = #{phone}

select gu.user_id as userId,gu.*
from g_user gu
where gu.nick_name = #{nickName}
